<script>
  let playerScore = 0;
  let computerScore = 0;
  let newGame = true;
  let animated = false;
  let player = 'rock';
  let computer = 'rock';
  let result = 'welcome';
  let playerChoice;

  function getComputerChoice() {
    let play = Math.random();

    play = play < 0.33 ? 'rock' : play > 0.66 ? 'paper' : 'scissors';

    return play;
  }

  function playRound() {
    animated = false;
    if (newGame) initNewGame();
    player = playerChoice;
    computer = getComputerChoice();
    result = compare(player, computer);
    checkGameOver();
  }

  function initNewGame() {
    playerScore = 0;
    computerScore = 0;
    newGame = false;
  }

  function compare(p, c) {
    if (p === c) {
      return 'draw';
    } else if (
      (p === 'rock' && c === 'scissors') ||
      (p === 'paper' && c === 'rock') ||
      (p === 'scissors' && c === 'paper')
    ) {
      playerScore++;
      return '<<<';
    } else {
      computerScore++;
      return '>>>';
    }
  }

  function checkGameOver() {
    if (playerScore === 3) {
      result = 'you win!';
      newGame = true;
    } else if (computerScore === 3) {
      result = 'you lose!';
      newGame = true;
    }
  }
</script>

